import React from 'react'
import TextField from './Components/TextField'
import PrintItems from './Components/PrintItems'


class HomePage extends React.Component{


    constructor(){
        super()


    this.state = {

        list: [],
        text: ""

        }
    this.handleAdd = this.handleAdd.bind(this)
    this.ChangeText = this.ChangeText.bind(this)

    }

    ChangeText = async function(input){
        await this.setState({text: input})
    }

    handleAdd = async function(){

        // e.preventdefault();


        if(this.state.text == ""){
            return
        }

        const newItem = {
            id: this.state.list.length,
            value: this.state.text
        }


        this.setState(state => ({
            list: this.state.list.concat(newItem)
        }))

        console.log(this.state.list)

    }

    render(){
        return(
            <div>
                <TextField ChangeText = {this.ChangeText} handleAdd = {this.handleAdd} list = {this.state.list}/>
                <PrintItems items = {this.state.list}/>
            </div>
        )
    }

}

export default HomePage
