import React from 'react'
// import './TextField.css'


class TextField extends React.Component{


    constructor(props){
        super(props)

        this.state = {
            text: ""
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleSubmitComplete = this.handleSubmitComplete.bind(this)
        this.handleSubmitAll = this.handleSubmitAll.bind(this)
    }

        handleChange = async function(e){        
            await this.setState({text: e.target.value})
            await this.props.ChangeText(this.state.text)

        }

        handleSubmitAll(){
            this.props.ShowAllList()
        }

        handleSubmitComplete(){
            this.props.ShowCompletedList()
        }

        handleSubmit(e){

            this.props.handleAdd()
            this.setState({text: ""})
            this.props.clearText()


        }

    render(){

        return(
            <div className = "mx-auto">
                <h4>Enter List</h4>
                <input className = "focus:ring-2 focus:ring-blue-600"  type = "text" placeholder = "Enter To Do" onChange = {this.handleChange} value = {this.state.text}/>
                <button onClick = {this.handleSubmit}>Submit</button>
                <button onClick = {this.handleSubmitComplete}>Completed Items</button>
                <button onClick = {this.handleSubmitAll}>Show All</button>
            </div>
        )
    }
}

export default TextField
