import React from 'react'


class PrintItems extends React.Component{


    constructor(props){

        super(props)

        this.printAllList = this.printAllList.bind(this)
        this.HandleX = this.HandleX.bind(this)
    }

    HandleX(value){
        this.props.DeleteItem(value)
    }

    HandleTick(value){
        this.props.CompleteItem(value)

    }


    printCompletedList(){
        console.log("here is completed list: ",this.props.completed_list)
        console.log("this is the noraml list: ",this.props.items)

        return(
            <div className = "border-2 border-yellow-600 max-w-2xl mx-auto bg-white rounded-xl shadow-md flex-auto items-center space-x-4">
                <ul>
                {this.props.completed_list.map(item => <p key = {item.id}>
                Item: {item.value}
                <br/>
                Status: {item.completed}
                <br/>
                <button onClick = {() => this.HandleX(item.value)}>
                    X
                </button>
                <button>
                    {item.icon} 
                </button>
                </p>)
                }
            </ul>
            </div>

        )
    }


    printAllList(){

        return(
            <div className = "border-2 border-indigo-600 max-w-2xl mx-auto bg-white rounded-xl shadow-md flex-auto items-center space-x-4">
                <ul>
                {this.props.items.map(item => <p  key = {item.id}>
                Item: {item.value}
                <br/>
                Status: {item.completed}
                <br/>
                <button onClick = {() => this.HandleX(item.value)}>
                    X
                </button>
                <button onClick = {() => this.HandleTick(item.value)}>
                    {item.icon} 
                </button>
                </p>)
                }
            </ul>
            </div>

        )

    }

    render(){
        console.log("Is dasdait true: ",this.props.showCompletedList)
        if(this.props.showCompletedList === true & this.props.completed_list.length !== 0){
            return(
                <div>
                    {this.printCompletedList()}
                </div>
            )
        }

        else if(this.props.showCompletedList == false & this.props.items.length !== 0){
            return(
                <div>
                    {this.printAllList()}
                </div>
            )
        }
        else {
            return(
                <div>

                </div>
            )
        }

    }

    // render(){

    //     return(
    //         <div>
    //             {this.printAllList()}
    //             {/* {this.printCompletedList()} */}
    //         </div>
    //     )
    // }

}

export default PrintItems
