class PrintItems extends React.Component{


    constructor(props){

        super(props)

        this.printAllList = this.printAllList.bind(this)
        this.HandleX = this.HandleX.bind(this)
    }

    HandleX(value){
        this.props.DeleteItem(value)
    }


    printAllList(){

        return(
            <ul>{this.props.items.map(item => <p key = {item.id}>
                {
                item.value}
                <button onClick = {() => this.HandleX(item.value)}>
                    X
                </button>
                <button>
                âœ… 
                </button>
                </p>)
                }
            </ul>
        )

    }

    render(){

        return(
            <div>
                {this.printAllList()}
            </div>
        )
    }

}

export default PrintItems
