import React from 'react'
import './TextField.css'


class TextField extends React.Component{


    constructor(props){
        super(props)

        this.state = {
            text: ""
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleSubmit = this.handleSubmitComplete(this)
    }

        handleChange = async function(e){        
            await this.setState({text: e.target.value})
            await this.props.ChangeText(this.state.text)

        }

        handleSubmitComplete(){
            this.props.ShowCompletedList()
        }

        handleSubmit(e){

            this.props.handleAdd()
            this.setState({text: ""})
            this.props.clearText()


        }

    render(){

        return(
            <div className = "TextField">
                <h4>Enter List</h4>
                <input type = "text" placeholder = "Enter To Do" onChange = {this.handleChange} value = {this.state.text}/>
                <button onClick = {this.handleSubmit}>Submit</button>
                <button onClick = "">Completed Items</button>
            </div>
        )
    }
}

export default TextField
