import React from 'react'
import TextField from './Components/TextField'
import PrintItems from './Components/PrintItems'


class HomePage extends React.Component{


    constructor(){
        super()

    this.state = {

        list: [],
        text: "",
        completed_list: []

    }

    this.handleAdd = this.handleAdd.bind(this)
    this.ChangeText = this.ChangeText.bind(this)
    this.clearText = this.clearText.bind(this)
    this.DeleteItem = this.DeleteItem.bind(this)
    this.GetCompletedList = this.GetCompletedList.bind(this)

    }

    GetCompletedList(){

        var list_temp = []
        console.log("here")

        this.state.list.forEach(item => {

            const newItem = {

                id: list_temp.length,
                value: item.value,
                completed: item.completed

            }

            console.log("Home")

            if(item.completed === "true"){
                console.log("In IF True")
                list_temp.push(newItem)
            }

        })

        this.setState({completed_list: list_temp})

    }

    DeleteItem(value){

        // console.log("Value: ",value)
        var list_temp = []
        // console.log("List at start: ",this.state.list)

        this.state.list.forEach(item => {

            const newItem = {

                id: list_temp.length,
                value: item.value
            }   
            console.log("VALUE IS: ",newItem)

            if(item.value !== value){
                // console.log("Item.value: ",item.value)
                // console.log("IN IF STATeMENT FOR VALUE: ",value)
                list_temp.push(newItem)
            }
            else{
                console.log("in Else Statement when value is: ",value)
            }

        }

        )
        // console.log("Hey this is temp list: ",list_temp)
        // console.log("List at end: ",this.state.list)

        this.setState({list: list_temp})

    }

    ChangeText(input){
        this.setState({text: input})
    }

    clearText(){
        this.setState({text: ""})
    }

    handleAdd(){

        if(this.state.text === ""){
            return
        }

        const newItem = {
            id: this.state.list.length,
            value: this.state.text,
            completed: "false"
        }


        this.setState(state => ({
            list: this.state.list.concat(newItem)
        }))
    }

    render(){
        return(
            <div>
                <TextField ChangeText = {this.ChangeText} handleAdd = {this.handleAdd} clearText = {this.clearText} list = {this.state.list}/>
                <PrintItems GetCompletedList = {this.GetCompletedList}  DeleteItem = {this.DeleteItem} completed_list = {this.state.completed_list}  items = {this.state.list}/>
            </div>
        )
    }

}

export default HomePage
