import React from 'react'


class PrintItems extends React.Component{


    constructor(props){

        super(props)

        this.printAllList = this.printAllList.bind(this)
        this.HandleX = this.HandleX.bind(this)
    }

    HandleX(value){
        this.props.DeleteItem(value)
    }

    HandleTick(value){
        this.props.CompleteItem(value)
        this.props.ShowCompletedList()
    }


    printCompletedList(){
        // console.log("here is completed list: ",this.props.completed_list)

        return(
            <ul>{this.props.completed_list.map(item => <p key = {item.id}>
                Item: {item.value}
                <br/>
                Status: {item.completed}
                <br/>
                <button onClick = {() => this.HandleX(item.value)}>
                    X
                </button>
                {/* <button onClick = {() => this.HandleTick(item.value)}>
                    ✅ 
                </button> */}
                </p>)
                }
            </ul>
        )
    }


    printAllList(){

        return(
            <ul>{this.props.items.map(item => <p key = {item.id}>
                Item: {item.value}
                <br/>
                Status: {item.completed}
                <br/>
                <button onClick = {() => this.HandleX(item.value)}>
                    X
                </button>
                <button onClick = {() => this.HandleTick(item.value)}>
                    ✅ 
                </button>
                </p>)
                }
            </ul>
        )

    }

    render(){
        if(this.props.showCompletedList === true){
            return(
                <div>
                    {this.printCompletedList()}
                </div>
            )
        }

        else if(this.props.showCompletedList == false){
            return(
                <div>
                    {this.printAllList()}
                </div>
            )
        }

    }

    // render(){

    //     return(
    //         <div>
    //             {this.printAllList()}
    //             {/* {this.printCompletedList()} */}
    //         </div>
    //     )
    // }

}

export default PrintItems
